const DEFAULT_SIZES = {
  mobile: 20,
  tablet: 24,
  desktop: 32,
};

export type IconType = keyof typeof icons;

type IconProps = {
  type: IconType;
};

export default function Icon({ type }: IconProps) {
  const { isMobile, isTablet } = useBreakpoints();
  if (isMobile) return icons[type](DEFAULT_SIZES["mobile"]);
  else if (isTablet) return icons[type](DEFAULT_SIZES["tablet"]);
  return icons[type](DEFAULT_SIZES["desktop"]);
}
